configvm=new Vue({el:"#j_config",data:{info:"",firmware:"",isupdate:""},ready:function(){this.getinfo(),this.getver()},methods:{getver:function(){var t=this;$.ajax({url:juli.URL.getversion,type:"get",async:!1,cache:!1}).done(function(e){t.firmware=e,t.info.firmwareVersion<t.firmware[0]&&(t.isupdate=!0,clientCreate(onConnectCallback))})},update:function(){var t=this;return this.info.electricity<25?($.toast("电量低于25%，请充电后再升级哦~","text"),!1):(boxUpgrade(t.firmware[0],t.firmware[1]),void(t.isupdate=!1))},getinfo:function(){if(!utils.online())return $.toast.prototype.defaults.duration=6e5,$.toast("故事机不在线！","text"),!1;var t=this;$.ajax({url:juli.URL.getinfo,type:"get",async:!1,cache:!1,data:{deviceId:utils.getdevice()}}).done(function(e){e.id=e.id.split("_")[0],e.cardAvailable=e.cardAvailable+"MB/",e.cardTotal=e.cardTotal+"MB",e.electricity>100?e.electricity="100%":e.electricity+"%",t.info=e})}}});